			[17] = { str = "Go down to #COORD[45,65]# and touch the wrecked boat to turn in #INThe Missing Courier pt.2# accept #GETBoat Wreckage#", x = 45, y = 65, zone = "Feralas" },
			[18] = { str = "Hearth to Feathermoon" },
			[19] = { str = "Go in the little building across from the Inn on the 2nd floor, turn in #INBoat Wreckage# accept #GETThe Knife Revealed#" },
			[20] = { str = "Go up top of the big spiral tower, turn in #INThe Knife Revealed# accept #GETPsychometric Reading#" },
			[21] = { str = "Go in the little building across form the Inn on the 2nd floor, turn in #INPsychometric Reading# accept #GETThe Woodpaw Gnolls#" },
			[22] = { str = "Take the boat to the mainland" },
			[23] = { str = "Go into the yeti cave at #COORD[56,56]# and kill yeti’s for #DOThe Mark of Quality# you can skin them and it’s also a drop.", x = 56, y = 56, zone = "Feralas" },
			[24] = { str = "If a #NPCOOX-22/FE Distress Beacon# has fallen accept the quest #GETFind OOX-22/FE!# and turn it in at the back of the cave SKIP the escort. " },
			[25] = { str = "Grind east and kill the Brutes around #COORD[60,56]#, and warlocks and shamans around #COORD[59,66]# for #DOThe High Wilderness# I kill any I see", x = 60, y = 56, zone = "Feralas" },
			[26] = { str = "Go up to #COORD[65,45]# and accept #GETFreedom For All Creatures# It’s pretty hidden, just NW of the camp entrance is a path that doesn’t even look like you can walk on it. To do this simply escort her to the cage, open it and kill anything until it says complete. ", x = 65, y = 45, zone = "Feralas" },
			[27] = { str = "Hand it back in and accept #GETDoling Justice pt.1# The mobs can be found in this camp." },
			[28] = { str = "Turn in #INDoling Justice pt.1# at #COORD[65,45]# and accept #GETDoling Justice pt.2#", x = 65, y = 45, zone = "Feralas" },
			[29] = { str = "Go down to #COORD[73,56]# and touch Large Leather Backpacks on the big tree. Turn in #INThe Woodpaw Gnolls# accept #GETThe Writhing Deep#", x = 73, y = 56, zone = "Feralas" },
			[30] = { str = "Right click the Backpack, accept #GETThalanaar Delivery#" },
			[31] = { str = "Grind down to the hive at #COORD[73,63]# and go all the way down. Go into the southern tunnel and stay right, You’ll see a Zukk’Ash Pod there. Turn in #INThe Writhing Deep# accept #GETFreed From the Hive#", x = 73, y = 63, zone = "Feralas" },
			[32] = { str = "Hearth to Feathermoon" },
			[33] = { str = "On the water side of the Gazebo, turn in #INThe Mark of Quality# accept #GETImproved Quality#" },
			[34] = { str = "Go in the small house across from the Inn, turn in #INThe High Wilderness# then go upstairs and turn in #INFreed From the Hive# accept #GETA Hero’s Welcome#" },
			[35] = { str = "Go to the big building next door and turn in #INA Hero’s Welcome# accept #GETRise of the Silithid#" },
			[36] = { str = "Fly out to Thalanaar (spot between 1k needles and here) turn in #INThalanaar Delivery# at #COORD[89,46]#", x = 89, y = 46, zone = "Feralas" },
			[37] = { str = "Fly to Rut'theran Village (Darnassus)" },
			[38] = { str = "Follow the dock and go in the house, turn in #INHandle With Care# accept #GETFavored of Elune?#" },
			[39] = { str = "Go upstairs, turn in #INIn Search of Knowledge# then go to the back of the house on the 2nd floor, open the green book and accept #GETFeralas: A History# turn it back in to her, accept #GETThe Borrower#" },
			[40] = { str = "Get new skills" },
			[41] = { str = "Go to the temple of the moon at #COORD[39,86]# and up to the 2nd floor, turn in #INRise of the Silithid# and #INDoling Justice pt.2#", x = 39, y = 86, zone = "Feralas" },
			[42] = { str = "Buy 2 Elixir of Fortitude for a later part of this quest" },
			[43] = { str = "Hearth to Feathermoon and fly to Gadgetzan and turn in #INThe Borrower# accept #GETThe Super Snapper FX#" },
		}
	},

-----------45-46 Uldaman
	--[407] = {
	[4546] = {
		title = "45-46 Uldaman",
		--n = "45-46 Uldaman",
		--pID = 406, nID = 408,
		--itemCount = 7,
		items = {
			[1] = { str = "45-46 Uldaman" },
			[2] = { str = "Now you can either grind on the gnolls in Feralas around where you found the backpack on the tree, or you can do Uldaman. I highly suggest you do Uldaman. You picked up some quests earlier, there are a few more in IF and in the badlands. If you get a good group build you can easily do just about every quest in there in under 3 hours max. I usually fully do Uldaman inside and out, I don’t finish all the quests unless I have to level. 1 full run inside and out should get you 75-100% through this level. I usually do this in about 3 hours no problem. Plus you can solo all the mobs outside of the instance 3 at a time no problem. The end of Uldaman, after the boss, (mini disks quest) gives about 20k xp between that and talking to the ghostly npc who spawns in there, as well as gives some nice xp for quests later on. And you could miss some xp spots for later quests I do if you skip this." },
			[3] = { str = "I don’t list all the quests for Uldaman because there is a good handful and you don’t have to do them all to fully level, and again it is optional. This is much faster than grinding would be. I really hate to do instances when trying for the best time but alliance has less quests than the horde do and I did mention there will be about 10-15% of grinding on your way to 60. If you did do all instances though you would be really slowed down. This isn’t going to put a break in your time with a good group, preferrably guild groups of 45+" },
			[4] = { str = "You can stop at about 75% to 46 but again I suggest you level. It’s not as bad as it seems." },
			[5] = { str = "A great map of Uldaman and where the parts of most of the quests pieces can be done at is listed here on worldofwar.net" },
			[6] = { str = "Once you’re all set, fly to SW and turn in #INIn Search of The Temple# at #COORD[64,20]# and accept #GETIn Search of The Hinterlands# get new skills then fly to Southshore.", x = 64, y = 20, zone = "Stormwind" },
			[7] = { str = " Run up to the path at #COORD[84,33]# behind Durnholde Keep (you’ll see 2 griffins) into The Hinterlands", x = 84, y = 33, zone = "Tarren Mill" },
		}
	},

-----------46-47 The Hinterlands
	--[408] = {
	[4647] = {
		title = "46-47 The Hinterlands",
		--n = "46-47 The Hinterlands",
		--pID = 407, nID = 409,
		--itemCount = 29,
		items = {
			[1] = { str = "46-47 The Hinterlands" },
			[2] = { str = "Go up to #COORD[11,46]# and turn in #INIn Search of The Hinterlands# accept #INGryphon Master Talonaxe#", x = 11, y = 46, zone = "The Hinterlands" },
			[3] = { str = "Get the FP at #COORD[11,46]# way up top then go in the cave and turn in #INGryphon Master Talonaxe#, accept #GETRhapsody Shindigger# and #GETWitherbark Cages#", x = 11, y = 46, zone = "The Hinterlands" },
			[4] = { str = "Go into the Inn and turn right, accept #GETSkulk Rock Clean-Up# and #GETTroll Necklace Bounty#" },
			[5] = { str = "Go way in the back of the Inn up to around #COORD[13,41]# and make Aerie Peak your home.", x = 13, y = 41, zone = "The Hinterlands" },
			[6] = { str = "Grind east to #COORD[20,48]# to the path while keeping an eye out for feathers on the ground for #DOFavored of Elune?# You don’t need them all yet", x = 20, y = 48, zone = "The Hinterlands" },
			[7] = { str = "Follow the path up to #COORD[26,48]# and turn in #INRhapsody Shindigger# accept #GETRhapsody's Kalimdor Kocktail#", x = 26, y = 48, zone = "The Hinterlands" },
			[8] = { str = "Go back down and to the troll camp and clear most of them out at #COORD[23,58]# and open the 2 cages for #DOWitherbark Cages#", x = 23, y = 58, zone = "The Hinterlands" },
			[9] = { str = "Grind east to #COORD[31,57]# and open the 3rd cage for #DOWitherbark Cages#", x = 31, y = 57, zone = "The Hinterlands" },
			[10] = { str = "You should also have 5 necklaces for #DOTroll Necklace Bounty# now" },
			[11] = { str = "Go NE up to #COORD[46,42]#, collecting feathers if you still need them on the way, and kill the Green Sludges for #DOSkulk Rock Clean-Up pt.1#", x = 46, y = 62, zone = "The Hinterlands" },
			[12] = { str = "Go to #COORD[49,37]# if you found OOX-09/HL Distress Beacon", x = 49, y = 37, zone = "The Hinterlands" },
			[13] = { str = "Go to #COORD[56,41]# and kill Jade Oozes for part 2 of #DOSkulk Rock Clean-Up#", x = 56, y = 41, zone = "The Hinterlands" },
			[14] = { str = "Go to the waterfall at #COORD[81,46]# and dive into the water below, then go south along this shore until you see Gammerita and take her picture for #DOThe Super Snapper FX# Also you want to keep an eye out for bottles along the shore for #DOWhiskey Slim's Lost Grog#", x = 81, y = 46, zone = "The Hinterlands" },
			[15] = { str = "Hearth to Aerie Peak" },
			[16] = { str = "Go to the front of the Inn and turn in #INSkulk Rock Clean-Up# and #INTroll Necklace Bounty# and any extra 5 necklaces you have." },
			[17] = { str = "Go up past the FP in the cave at 9,44 and turn in #INWitherbark Cages# accept #GETThe Altar of Zul#", x = 9, y = 44, zone = "The Hinterlands" },
			[18] = { str = "Go run up top of the Altar of Zul at #COORD[48,68]# for #DOThe Altar of Zul#", x = 48, y = 68, zone = "The Hinterlands" },
			[19] = { str = "Now I grind on the trolls and wolves around Zul until I’m 1 bar from level 47. You should be at least half way through level 46 at this point." },
			[20] = { str = "Get the quest #GETJammal'an the Prophet# at #COORD[33,75]# if you plan to do Sunken Temple later.", x = 33, y = 75, zone = "The Hinterlands" },
			[21] = { str = "I’m sure by the time you near level 47 you’re going to find OOX-09/HL Distress Beacon accept #GETFind OOX-09/HL# and turn it in at #COORD[49,37]# and then accept #GETRescue OOX-09/HL# and escort it to #COORD[78,61]# Theres only 2 encounters of 3 mobs, but watch out for the stealthed wolves", x = 49, y = 37, zone = "The Hinterlands" },
			[22] = { str = "Once you’re about 6k or 1 bar from level 47 hearth to Aerie Peak" },
			[23] = { str = "Turn in any #INTroll Necklace Bounty# you have just before you go out" },
			[24] = { str = "Go up past FP to 9,44 and turn in #INThe Altar of Zul# accept #GETThadius Grimshade#", x = 9, y = 44, zone = "The Hinterlands" },
			[25] = { str = "Fly to Menethil Harbor and take the boat to Auberdine, then fly to Rut’theran Village" },
			[26] = { str = "Run to the house at #COORD[55,92]# and turn in #INFavored of Elune?#", x = 55, y = 92, zone = "The Hinterlands" },
			[27] = { str = "Go upstairs, turn in #INThe Super Snapper FX# accept #GETReturn to Troyas#" },
			[28] = { str = "You should now be level 47 and about 8k into it." },
			[29] = { str = "Fly to Feralas" },
		}
	},

-----------47-47 Feralas
	--[409] = {
	[4747] = {
		title = "47-47 Feralas",
		--n = "47-47 Feralas",
		--pID = 408, nID = 410,
		--itemCount = 22,
		items = {
			[1] = { str = "47-47 Feralas" },
			[2] = { str = "Make Feathermoon Stronghold your home" },
			[3] = { str = "Go across from the Inn to the 2 story house, turn in #INReturn to Troyas# accept #GETThe Stave of Equinex#" },
			[4] = { str = "Accept #GETThe Sunken Temple# Next to him" },
			[5] = { str = "Swim or sail east to the dock." },
			[6] = { str = "Beside the dock at #COORD[44,33]# accept #GETZapped Giants# and #GETFuel for Zapping#", x = 44, y = 33, zone = "Feralas" },
			[7] = { str = "Make the Zorbin's Ultra-Shrinker a hotkey. Then go south along the shore and start zapping giants and killing them for #DOZapped Giants# and kill water elementals for #DOFuel for Zapping#" },
			[8] = { str = "Go back to #COORD[44,33]# and turn in #INZapped Giants# and #INFuel for Zapping# You can now do those again, but I wouldn’t, for about 500 xp per turn in. Now he opens his store to you.", x = 44, y = 33, zone = "Feralas" },
			[9] = { str = "Run over to the grimtotem camp at #COORD[65,45]# and accept #GETAn Orphan Looking For A Home#", x = 65, y = 45, zone = "Feralas" },
			[10] = { str = "Go down to the yeti cave at #COORD[56,56]# and do the robot chicken escort if you can, don’t be afraid to ask for help if needed.", x = 56, y = 56, zone = "Feralas" },
			[11] = { str = "Grind to the yeti cave at #COORD[51,31]# on Ironfur Bears and Groddoc Gorillas until you get 3 livers from each of them for #DORhapsody's Kalimdor Kocktail#", x = 51, y = 31, zone = "Feralas" },
			[12] = { str = "Kill the Rage Scar Yeti’s here for #DOImproved Quality# remember you can skin them for these as well. If a Pristine Yeti Hide drops, accept the quest it gives #GETPristine Yeti Hide#" },
			[13] = { str = "Go to Rockbiter at #COORD[42,22]# and accept #GETThe Giant Guardian#", x = 42, y = 22, zone = "Feralas" },
			[14] = { str = "Do #DOThe Stave of Equinex# by doing the following. Get the Blytan Essence from the fire at #COORD[38,15]# #DOThe Stave of Equinex# Get the Samha Essence from the fire at #COORD[40,12]# (atop the ruined arch) to get to it, go around back and hop up the broken rocks (it’s not easily noticed) Go back out the back and Grab the Imbel Essence from the fire at 39,9. Get the Lahassa Essence from the fire at #COORD[37,12]# under the brown gazebo. Go down to the Equinex Monolith just over the cliff. Use Troya’s Staff next to it. Touch the Monolith and turn in #INThe Stave of Equinex# accept #GETThe Morrow Stone#", x = 38, y = 15, zone = "Feralas" },
			[15] = { str = "Go back up near the previous flame to #COORD[38,10]# and turn in #INThe Giant Guardian# accept the escort #GETWandering Shay#", x = 38, y = 10, zone = "Feralas" },
			[16] = { str = "Grab the bell from the chest and hotkey it. This is similar to the druid quest back in Darkshore. Run her back to Rockbiter at #COORD[42,21]# and turn in #INWandering Shay# (the trick is to keep ahead of her, if she’s with you by your side she will wander off a lot more and the bell has a 1 minute timer.", x = 42, y = 21, zone = "Feralas" },
			[17] = { str = "Hearth to Feathermoon Stronghold" },
			[18] = { str = "Go to the gazebo near the water beside the hippogryph master, turn in #INPristine Yeti Hide# and #INImproved Quality#" },
			[19] = { str = "Take your 2 Elixirs of Fortitude out of your mailbox if you don’t already have them on you." },
			[20] = { str = "Go across to the 2 story house, turn in #INThe Morrow Stone#" },
			[21] = { str = "You should be close to 70% into 47" },
			[22] = { str = "Fly to Gadgetzan" },
		}
	},

-----------47-48 Tanaris
	--[410] = {
	[4748] = {
		title = "47-48 Tanaris",
		--n = "47-48 Tanaris",
		--pID = 409, nID = 411,
		--itemCount = 42,
		items = {
			[1] = { str = "47-48 Tanaris" },
			[2] = { str = "Just inside Gadgetzan to the right, accept #GETThe Thirsty Goblin#" },
			[3] = { str = "Make Gadgetzan your home" },
			[4] = { str = "Go behind the Inn, accept #GETThe Dunemaul Compound#" },
			[5] = { str = "Near the north entrance, accept #GETThistleshrub Valley#" },
			[6] = { str = "Go south to the skeleton in the ground at #COORD[49,36]# and #COORD[44,39]# and #COORD[47,45]# kill Rocs for #DORhapsody's Kalimdor Kocktail# If it’s busy just do it later.", x = 49, y = 36, zone = "Tanaris" },
			[7] = { str = "Ride over to #COORD[52,45]# turn in #INThe Sunken Temple# and accept #GETThe Stone Circle# and #GETGahz'Ridian#", x = 52, y = 45, zone = "Tanaris" },
			[8] = { str = "Now put on the Gahz’Ridian Detector Helm and yellow dots on mini map will show the pieces you need to get for #DOGahz'Ridian#. It’s up to you if you want to wear this in fights. This stuff is all over" },
			[9] = { str = "Run over west to the Dunemaul Compound clear a path to #COORD[40,58]# and kill Gor’Marok the Ravager for #DOThe Dunemaul Compound# You don’t need the 20 ogre kills all from here", x = 40, y = 58, zone = "Tanaris" },
			[10] = { str = "Head over to #COORD[47,65]# and finish killing the ogres for The Dunemaul Compound while gathering the rest of the artifacts for Gahz'Ridian", x = 47, y = 65, zone = "Tanaris" },
			[11] = { str = "Grind on the ogres until you’re #COORD[10,00]#0 from level 48" },
			[12] = { str = "Sneak up the left side of Uldum and go up to the pedestal at #COORD[37,81]# and turn in #INSeeing What Happens#", x = 37, y = 81, zone = "Tanaris" },
			[13] = { str = "Talk to the guy who appears, accept #GETThe Stone Watcher# and talk to him until completed. Then touch the pedastal to turn it back in" },
			[14] = { str = "Touch the pedastal one more time and accept #GETReturn to Ironforge#" },
			[15] = { str = "Go west to #COORD[29,66]# and kill Gnarled Thistlegrub and Thistlegrub Rootshapers for #DOThistleshrub Valley# and the dew collectors for #DOThe Thirsty Goblin# You should hit level 48 here", x = 29, y = 66, zone = "Tanaris" },
			[16] = { str = "If you find Tooga, accept #GETTooga’s Quest# I found him at #COORD[29,73]# near the zepplin, but you can find him anywhere around this valley. He follows you, you don’t have to follow him ", x = 29, y = 73, zone = "Tanaris" },
			[17] = { str = "Go up to the Noxious Lair around #COORD[34,46]# and kill Centipaar there for #DONoxious Lair Investigation# If you can’t get them all before tooga has 10 minutes left, go turn that in and then come back.", x = 34, y = 76, zone = "Tanaris" },
			[18] = { str = "Turn in #INTooga’s Quest# near steamwheedle port at #COORD[66,25]#", x = 66, y = 25, zone = "Tanaris" },
			[19] = { str = "Accept #GETYuka Screwspigot#" },
			[20] = { str = "Turn in #INScreecher Spirits# at #COORD[66,22]# SKIP the ZF part", x = 66, y = 22, zone = "Tanaris" },
			[21] = { str = "Hearth to Gadgetzan" },
			[22] = { str = "Go near the entrance and accept #GETThe Thirsty Goblin# back and turn it in accept #GETIn Good Taste#" },
			[23] = { str = "Go behind the Inn near the fire, turn in #INThe Dunemaul Compound#" },
			[24] = { str = "Go near the north entrance, hand in #INThistleshrub Valley#" },
			[25] = { str = "Go west to Sprinkle, turn in #INIn Good Taste# accept #GETSprinkle's Secret Ingredient#" },
			[26] = { str = "In the building behind her, turn in #INNoxious Lair Investigation#" },
			[27] = { str = "Go on the hill behind the building, accept #GETThe Schrimshank Redemption#" },
			[28] = { str = "Run down to #COORD[52,45]# and turn in #INGahz'Ridian#", x = 52, y = 45, zone = "Tanaris" },
			[29] = { str = "Run down to the entrance at #COORD[55,70]# take the spiral down to the entrance that is north. first fork go right. after the circular room at the next fork go right. You will enter a big square room. Go right to #COORD[55,71]# and grab the survey gear for #DOThe Schrimshank Redemption#", x = 55, y = 70, zone = "Tanaris" },
			[30] = { str = "Die on purpose so you end up at Gadgetzan" },
			[31] = { str = "Go to the hill on the west side of town, turn in #INThe Schrimshank Redemption# accept #GETInsect Part Analysis pt.1#" },
			[32] = { str = "Go to the house on the NW part of town, turn in #INInsect Part Analysis pt.1# accept #GETInsect Part Analysis pt.2#" },
			[33] = { str = "Go back up on the hill, turn in #INInsect Part Analysis pt.2# accept #GETRise of the Silithid#" },
			[34] = { str = "Run up to the Shimmering Flats to #COORD[78,74]# and turn in #INAn Orphan Looking For A Home# accept #GETA Short Incubation# and since you already have this, turn it back in and accept #GETThe Newest Member of the Family# now you have 1 hour.", x = 78, y = 74, zone = "Thousand Needles" },
			[35] = { str = "Fly to Theramore and get any mageweave you have out and build up first aid as much as you can." },
			[36] = { str = "Sail to Menethil and fly to IF" },
			[37] = { str = "Make IF your home and get new skills" },
			[38] = { str = "Go to #COORD[77,11]# and turn in #INReturn to Ironforge# accept #GETA Future Task# and #GETPassing the Burden#", x = 77, y = 11, zone = "Ironforge" },
			[39] = { str = "Turn around to #COORD[70,18]# and turn in #INA Future Task#", x = 70, y = 18, zone = "Ironforge" },
			[40] = { str = "Go to 31,4 and turn in #INPassing the Burden# accept #GETArcane Runes# and #GETAn Easy Pickup#", x = 31, y = 4, zone = "Ironforge" },
			[41] = { str = "Go to #COORD[70,93]#, turn in #INAn Easy Pickup# accept #GETSignal For Pickup# then turn it back in. Now you got the flare gun to signal later on.", x = 70, y = 93, zone = "Ironforge" },
			[42] = { str = "Fly to the Hinterlands" },
		}
	},

-----------48-48 The Hinterlands
	--[411] = {
	[4848] = {
		title = "48-48 The Hinterlands",
		--n = "48-48 The Hinterlands",
		--pID = 410, nID = 412,
		--itemCount = 8,
		items = {
