-----------48-49 Stranglethorn Vale
	--[412] = {
	[4849] = {
		title = "48-49 Stranglethorn Vale",
		--n = "48-49 Stranglethorn Vale",
		--pID = 411, nID = 413,
		--itemCount = 27,
		items = {
			[1] = { str = "48-49 Stranglethorn Vale" },
			[2] = { str = "Go down to the first floor of the Inn, make it your home" },
			[3] = { str = "Turn in #INBack To Booty Bay# and #INWhiskey Slim's Lost Grog#" },
			[4] = { str = "Enter the house next to the upside down, half ship, turn in #INDeliver to MacKinley#" },
			[5] = { str = "Under that boat outside, accept #GETStranglethorn Fever#" },
			[6] = { str = "Go up the ramp and first house past the bridge, turn in all of the robot chicken escorts you’ve gotten. I didn’t get the Tanaris one while writing this guide so I missed out on the quest to get 7100 extra exp" },
			[7] = { str = "Near the boat, accept #GETThe Captain's Chest#" },
			[8] = { str = "Go kill gorillas around #COORD[34,63]# for #DOStranglethorn Fever# then go up into the cave at #COORD[35,60]# turn it in. the guy in the cave asks for 10 gorilla fangs, then he summons 3 waves of gorillas. Mokk is in the 3rd wave. They’re all around level 42-44 it starts with 3, then 5 then I think 6 or 7. It can get tough. Just kill Mokk first in case you die.", x = 35, y = 60, zone = "Stranglethorn Vale" },
			[9] = { str = "Go up to #COORD[38,34]# and kill #NPCKing Bangalash# for #DOBig Game Hunter# he spawns level 33 minions. You can kill him before a few come out.", x = 38, y = 34, zone = "Stranglethorn Vale" },
			[10] = { str = "Run up to #COORD[48,25]# and kill the elite ogres all around here until Maury’s Key drops for #DOCracking Maury’s Foot# If you don’t find this after an hour just abandon the quest. It has a low drop rate. I grind until i'm 2 bars from level 49 if I don’t get the key. Usally it’s about an hours worth.", x = 48, y = 25, zone = "Stranglethorn Vale" },
			[11] = { str = "Go up to Nessingways at #COORD[35,10]# and turn in #INBig Game Hunter#", x = 35, y = 10, zone = "Stranglethorn Vale" },
			[12] = { str = "Hearth to Booty Bay, run up to the half boat just outside the Inn" },
			[13] = { str = "Turn in #INStranglethorn Fever#" },
			[14] = { str = "Run to the shore just east of BB and open blue bottles until you get Carefully Folded Note which starts #GETMessage in A Bottle pt.1#" },
			[15] = { str = "Go to #COORD[36,69]# and kill #NPCGorlash# for #DOThe Captain's Chest# He’s a 47 elite, it can be tough but I did it. Use everything you have", x = 36, y = 69, zone = "Stranglethorn Vale" },
			[16] = { str = " Go to #COORD[38,80]# and turn in #INMessage in A Bottle pt.1# SKIP the next part, unless you can get a group to kill the 51 ape", x = 38, y = 80, zone = "Stranglethorn Vale" },
			[17] = { str = "Go down to the pirate ships, 1 is at #COORD[33,87]# and the other 2 are at #COORD[30,88]# You want to go to the 2nd floor near the front of the ship and kill the captain of each for #DOThe Bloodsail Buccaneers pt.5#", x = 33, y = 87, zone = "Stranglethorn Vale" },
			[18] = { str = "Look for a scroll on one of the ships called Cortello's Riddle which starts #GETCortello's Riddle pt.1#" },
			[19] = { str = "If hearth is up, use it, otherwise die so you end up at BB" },
			[20] = { str = "Go near the boat, turn in #INThe Captain's Chest#" },
			[21] = { str = "Go up top of the Inn, turn in #INThe Bloodsail Buccaneers pt.5#" },
			[22] = { str = "Fly to SW and go to #COORD[64,20]# turn in #INRhapsody's Tale# SKIP the rest", x = 64, y = 20, zone = "Stormwind" },
			[23] = { str = "From here on, either look at the AH when you go by one, or use your AH char to search for your Beaststalker Bracers. I got mine for 8g also look for a mithril casing for later in Un’Goro" },
			[24] = { str = "Fly to Duskwood" },
			[25] = { str = "Run east through Deadwind Pass to the Swamp of Sorrows" },
			[26] = { str = "Follow the road until it forks and the left fork has a bridge at #COORD[22,48]# and under it is A Soggy Scroll which turns in #INCortello's Riddle pt.1# accept #GETCortello's Riddle pt.2#", x = 22, y = 48, zone = "Swamp of Sorrow" },
			[27] = { str = "Run down to the Blasted Lands" },
		}
	},
	
--------49-50 Blasted Lands
	--[413] = {
	[4950] = {
		title = "49-50 Blasted Lands",
		--n = "49-50 Blasted Lands",
		--pID = 412, nID = 501,
		--itemCount = 30,
		items = {
			[1] = { str = "49-50 Blasted Lands" },
			[2] = { str = "No need to rush through this zone, so grind on anything you see." },
			[3] = { str = "Stop at #COORD[50,14]# and accept the following 5 quests: #GETThe Basilisk's Bite# #GETVulture's Vigor# #GETSnickerfang Jowls# #GETA Boar's Vitality# and #GETThe Decisive Striker#", x = 50, y = 14, zone = "Blasted Lands" },
			[4] = { str = "You’re going to need the following amount of items to complete all 5 of these quests. Snickerfang Jowls:5, Blasted Boar Lungs:6, Scorpok Pincers:6, Basilisk Brain:11, Vulture Gizzards:14 It’s going to be like grinding getting all this so you’re killing 2 birds with 1 stone. You’ll most likely gain half a level before you turn these in." },
			[5] = { str = "Just circle the center of the map and you’ll find all the stuff you need to kill. I’d mark it with coords but there would be way too many. You might want to avoid anything level 53 and up which are farther to the south. You should make some nice money with skinning here." },
			[6] = { str = "You should be close to 3 ½ bars into level 49 now" },
			[7] = { str = "Grind down to #COORD[51,35]# and accept #GETEverything Counts In Large Amounts# If you do find one of these, hand it back in when you pass her, you’re bound to find at least one while here. Don’t even bother with the other quest you’ll Never find it. I’ve been playing WoW since release and never found one. ", x = 51, y = 35, zone = "Blasted Lands" },
			[8] = { str = "Go to #COORD[50,14]# and turn in the 5 quests, #INThe Basilisk's Bite# #INVulture's Vigor# #INSnickerfang Jowls# #INA Boar's Vitality# and #INThe Decisive Striker# but turn in the one you want the most last, because you get buffed and get an item as well. These each give 4700 xp", x = 50, y = 14, zone = "Blasted Lands" },
			[9] = { str = "Go to Nethergarde Keep to the top of the tall tower at #COORD[66,19]# and turn in #INThadius Grimshade# SKIP the next part.", x = 66, y = 19, zone = "Blasted Lands" },
			[10] = { str = "You should be close to 70% into level 49 now. If you don’t want to do ZF to hit 50, grind here until you’re about 13k from level 50. If you want do ZF go to the next step." },
			[11] = { str = "Hearth to Booty Bay" },
			[12] = { str = "Accept #GETZanzil's Mixture# and #GETA Fool's Stout#" },
			[13] = { str = "Get on the boat to Ratchet" },
			[14] = { str = "Accept #GETVolcanic Activity# at the first hut off the dock" },
			[15] = { str = "Just outside this hut is a green chest, open it for #DOThe Stone Circle#" },
			[16] = { str = "Fly to Tanaris" },
			[17] = { str = "Go near the NW entrance, turn in #INSprinkle's Secret Ingredient# accept #GETDelivery For Marin#" },
			[18] = { str = "Go back near the South entrance and turn in #INDelivery For Marin# accept #GETNoggenfogger Elixir# then turn it back in. You will recieve 5 elixirs and be able to purchase them." },
			[19] = { str = "Run in the Shimmering Flats to #COORD[77,77]# and turn in #INZanzil's Mixture# and #INA Fool's Stout# accept #GETGet the Goblins Drunk# go to #COORD[79,76]# and turn it in.", x = 77, y = 77, zone = "Thousand Needles" },
			[20] = { str = "Go back to #COORD[77,77]# and accept #GETReport Back to Fizzlebub#", x = 77, y = 77, zone = "Thousand Needles" },
			[21] = { str = "Go to #COORD[52,45]# and turn in #INThe Stone Circle SKIP the rest unless you plan on doing the Sunken Temple.", x = 52, y = 45, zone = "Thousand Needles" },
			[22] = { str = "You should be about 30k from level 50 now, like I said above, either go do ZF or you should have grinded in Blasted Lands." },
			[23] = { str = "If you do ZF you only really need to complete at least 2-3 of the following quests #DOTroll Temper# #DODivino-matic Rod# #DOGahz'rilla# #DOThe Prophecy of Mosh'Aru# I highly suggest getting #GETDivino-matic Rod# and #GETGahz'rilla# because you get an awesome ring and a mount speed inscrease trinket." },
			[24] = { str = "Now you should be about 1-2 bars into level 50 if you went to ZF. From here on out there is a lot more fresh content, as you seen 45-50 is a pain in the ass. We still have a lot of untouched areas to level in now." },
			[25] = { str = "Hearth to BB and turn in #INReport Back to Fizzlebub#" },
			[26] = { str = "Fly to IF to get new skills and accept the quest #GETThe Hunter's Charm#" },
			[27] = { str = "Make sure you get at least 15 Silk Cloth out of the mailbox or AH alt" },
			[28] = { str = "Make IF your home and accept #GETAssisting Arch Druid Staghelm#" },
			[29] = { str = "Fly to Thelsamar" },
			[30] = { str = "Run down into the Badlands all the way west to #COORD[01,62]# and enter the Searing Gorge", x = 1, y = 62, zone = "Badlands" },
		}
	},
}
